mapper.select:
SELECT

FROM exposure_config

  AND name LIKE CONCAT('%', #{name}, '%')
  AND platform = #{platform}
  AND config_type = #{configType}

ORDER BY create_time DESC

mapper.update:
UPDATE exposure_config
name = #{name}, platform = #{platform}, account = #{account}, config_type = #{configType}, search_keywords = #{searchKeywords}, comment_content = #{commentContent}, daily_limit = #{dailyLimit}, start_time = #{startTime}, sort_order = #{sortOrder}, skip_repeat = #{skipRepeat}, status = #{status},
WHERE id = #{id}
