mapper.select:
SELECT pc.id, pc.user_id1, pc.user_id2, pc.create_time, pc.update_time, pc.last_msg_id, pc.is_blocked, pc.del_flag, CASE WHEN pc.user_id1 = auth_user.id THEN pc.user_id2 ELSE pc.user_id1 END AS peer_user_id, peer.account AS peer_account, peer.nickname AS peer_nickname, peer.avatar AS peer_avatar, NULL AS peer_funds, auth_user.account AS douyin_id, MAX(pcm.send_time) AS last_send_time FROM fa_private_chat pc JOIN fa_private_chat_msg pcm ON pc.id = pcm.chat_id AND pcm.del_flag = 0 JOIN comment_user auth_user ON auth_user.id IN (pc.user_id1, pc.user_id2) JOIN comment_user peer ON peer.id = CASE WHEN pc.user_id1 = auth_user.id THEN pc.user_id2 ELSE pc.user_id1 END
AND auth_user.account = #{account}
AND pcm.send_time >= DATE_SUB(NOW(), INTERVAL #{hours} HOUR) AND pc.del_flag = 0
GROUP BY pc.id, pc.user_id1, pc.user_id2, pc.create_time, pc.update_time, pc.last_msg_id, pc.is_blocked, pc.del_flag, peer.id, peer.account, peer.nickname, peer.avatar, auth_user.id, auth_user.account ORDER BY last_send_time DESC LIMIT #{limit}

mapper.update:
update fa_private_chat
user_id1 = #{userId1}, user_id2 = #{userId2}, create_time = #{createTime}, update_time = #{updateTime}, last_msg_id = #{lastMsgId}, is_blocked = #{isBlocked}, peer_user_id = #{peerUserId}, peer_account = #{peerAccount}, peer_nickname = #{peerNickname}, peer_avatar = #{peerAvatar}, peer_funds = #{peerFunds}, last_send_time = #{lastSendTime}, douyin_id = #{douyinId}, del_flag = #{delFlag},
where id = #{id}
